import React from "react";
import Lesson from "./Lesson";

class Main extends React.Component {
  render() {
    const LessonList = [
      [
        {
          name: "HTML&CSS",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/html.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "JavaScript",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/es6.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "jQuery",
          image:
            "https://d2aj9sy12tbpym.cloudfront.net/assets/languages/language_list/jquery-a96de286d2c3b000bea702b018044b401b58cbbda3b4481af57f5bec5464ccf2.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "Ruby",
          image:
            "https://d2aj9sy12tbpym.cloudfront.net/assets/languages/language_list/ruby-67da22c74d14d226cbb854339c9d0d797440abb257eec16e51a00d9abba75d97.svg",
          introduction: "Webページの見た目を作る言語",
        },
      ],
      [
        {
          name: "HTML&CSS",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/html.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "JavaScript",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/es6.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "jQuery",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/html.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "jQuery",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/html.svg",
          introduction: "Webページの見た目を作る言語",
        },
      ],
      [
        {
          name: "HTML&CSS",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/html.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "JavaScript",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/es6.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "jQuery",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/html.svg",
          introduction: "Webページの見た目を作る言語",
        },
        {
          name: "jQuery",
          image:
            "https://s3-ap-northeast-1.amazonaws.com/progate/shared/images/lesson/react/html.svg",
          introduction: "Webページの見た目を作る言語",
        },
      ],
    ];

    return (
      <div className="main-wrapper">
        <div className="path-container">
          <p>パス</p>
          <h1>Web開発パス(Node.js)</h1>
          <p>
            このパスではWeb開発に必要な知識を学ぶことができます。
            Node.jsを使ってWebアプリの仕組みを理解し、実際に作れるようになりましょう！
          </p>
        </div>

        {/*language-path-list */}
        <div className="language-path-list">
          <div className="path-card">
            <div className="path-lesson">
              <img src="https://d18en44nkeqroq.cloudfront.net/paths/rails/top-image.svg" />
            </div>
          </div>
        </div>

        <div className="lesson-container">
          {/*Lesson1*/}
          {LessonList[0].map((lessonItem) => {
            return (
              <Lesson
                name={lessonItem.name}
                image={lessonItem.image}
                introduction={lessonItem.introduction}
              />
            );
          })}

          {/*Lesson2*/}
          {LessonList[1].map((lessonItem) => {
            return (
              <Lesson
                name={lessonItem.name}
                image={lessonItem.image}
                introduction={lessonItem.introduction}
              />
            );
          })}

          {/*Lesson3*/}
          {LessonList[2].map((lessonItem) => {
            return (
              <Lesson
                name={lessonItem.name}
                image={lessonItem.image}
                introduction={lessonItem.introduction}
              />
            );
          })}
        </div>
      </div>
    );
  }
}
export default Main;
