import React from "react";

class Lesson extends React.Component {
  constructor(props) {
    super(props);
    // stateの初期値を定義してください
  }
  render() {
    return (
      <div className="lesson-card">
        <div className="lesson-item">
          <h4>{this.props.name}</h4>
          <img src={this.props.image} />

          <div className="modal">
            <div className="modal-inner">
              <div className="modal-header"></div>
              <div className="modal-introduction">
                {/* レッスンの名前を表示してください */}
                <h2>{this.props.name}</h2>

                {/* レッスンの紹介文を表示してください */}
                <p>{this.props.introduction}</p>
              </div>
              <button className="modal-close-btn">とじる</button>
            </div>
          </div>

          <p className="lesson-num">
            <img src="https://d2aj9sy12tbpym.cloudfront.net/javascripts/dist/assets/lesson-book-471ff51241d3aa8b65f455a9bdeea4e0.svg" />
            <span>{this.props.num}記事</span>
          </p>
        </div>
      </div>
    );
  }
}

export default Lesson;
